import { FieldProps, FieldAttributes, useField } from "formik";
import React from "react";
import { TextInput } from "react-native-paper";

import * as yup from "yup";

interface InputFieldProps {}

// export const InputField: React.FC<InputFieldProps> = ({}) => {
//         return ();
// }

export const InputField = ({ field, ...props }: FieldProps) => {
  return <TextInput {...field} {...props} />;
};

export const MyTextField: React.FC<FieldAttributes<{}>> = ({
  placeholder,
  ...props
}) => {
  const [field, meta] = useField(props);
  //   const errorText = meta.error && meta.touched ? meta.error : "";
  return <TextInput placeholder={placeholder} {...(field as any)} />;
};

export const validationSchema = yup.object({
  firstName: yup.string().required().max(10),
  pets: yup.array().of(
    yup.object({
      name: yup.string().required(),
    })
  ),
});
