import { gql } from "apollo-boost";
import React, { useEffect } from "react";
import { Button, TextInput } from "react-native-paper";
import { LineBreak, StyledColumnView, Wrapper } from "../global-ui/ReusedUI";
import { AuthNavProps } from "../navigation/auth/AuthParamList";
import {
  useRegisterMutation,
  RegisterInput,
} from "../generated/apolloComponents";
import { useForm } from "react-hook-form";

interface RegisterViewProps {}

export const RegisterView: React.FC<AuthNavProps<"Register">> = ({
  navigation,
  route,
}) => {
  // form stuff
  const { register, setValue, handleSubmit, errors } = useForm();

  // register mutation
  const [registerUser, { loading }] = useRegisterMutation(registerInput);
  // TODO define what happens on submit
  const onSubmit = (data: RegisterInput) => {
    console.log(data);
    // actually log in so can get a cookie :)
    navigation.navigate("Login");
  };

  // how the form updates + validation
  useEffect(() => {
    register({ name: "username" });
    register({ name: "email" });
    register({ name: "password" });
    register({ name: "passwordConfirm" });
  }, [register]);

  return (
    <Wrapper>
      <StyledColumnView>
        <TextInput
          label="Username"
          mode="outlined"
          onChangeText={(text) => setValue("username", text)}
        />
        <TextInput
          label="Email"
          mode="outlined"
          onChangeText={(text) => setValue("email", text)}
        />
        />
        <TextInput
          label="Password"
          mode="outlined"
          onChangeText={(text) => setValue("password", text)}
        />
        <TextInput
          label="Re-enter Password"
          mode="outlined"
          onChangeText={(text) => setValue("passwordConfirm", text)}
        />
        <LineBreak />
        <Button mode="contained" onPress={handleSubmit(onSubmit)}>
          SIGN UP
        </Button>
      </StyledColumnView>
    </Wrapper>
  );
};
