import { Formik, Field } from "Formik";
import React from "react";
import { Button, TextInput } from "react-native-paper";
import { useRegisterMutation } from "../generated/apolloComponents";
import { LineBreak, StyledColumnView, Wrapper } from "../global-ui/ReusedUI";
import { AuthNavProps } from "../navigation/auth/AuthParamList";
import { InputField } from "./InputField";

interface RegisterViewProps {}

export const RegisterView: React.FC<AuthNavProps<"Register">> = ({
  navigation,
  route,
}) => {
  // register mutation
  const [registerUser, { loading, error }] = useRegisterMutation();
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  async function submitRegisterUser(formData: any, setErrors: any) {
    try {
      const response = await registerUser({
        variables: {
          data: {
            // username: formData.username,
            // password: formData.password,
            email: formData.email,
            username: "xcfvgbhjnkmfghjk",
            password: "dfghjklcvbnm,",
          },
        },
      });
      console.log(response);
    } catch (err) {
      // console.log("err: ", error.graphQLErrors);
      const errors: { [key: string]: string } = {};
      err.graphQLErrors[0].extensions.exception.validationErrors.forEach(
        (validationErr: any) => {
          Object.values(validationErr.constraints).forEach((message: any) => {
            errors[validationErr.property] = message;
          });
        }
      );
      console.log(errors);
      setErrors(errors);
    }
  }

  return (
    <Formik
      initialValues={{ email: "" }}
      onSubmit={(values, { setErrors }) =>
        submitRegisterUser(values, { setErrors })
      }>
      {({ handleChange, handleBlur, handleSubmit, values }) => (
        <Wrapper>
          <StyledColumnView>
            <TextInput
              label="email"
              onChangeText={handleChange("email")}
              onBlur={handleBlur("email")}
              value={values.email}
            />

            <Field name="username" label="username" component={InputField} />
            {/* TODO figure out password hiding  */}
            <Field name="password" label="password" component={InputField} />
          </StyledColumnView>
          <LineBreak />

          <Button onPress={handleSubmit as any}>Submit</Button>
        </Wrapper>
      )}
    </Formik>
  );
};
